import Vue from 'vue'
import Vuex from 'vuex'
import {reqCategoryList} from '@/api'
Vue.use(Vuex)
const state ={}
const mutations ={}
const actions ={
    categoryList(){
        let result =  reqCateGoryList().then(
            res=>{
            console.log("res")
            console.log(res)
            return res
            }
        )
        console.log("result")
        console.log(result)
    }
}
const getters ={}
//对外暴露store的一个实例
export default new Vuex.Store({
    state,
    mutations,
    actions,
    getters
    
})
